import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AppComponent } from 'src/app/app.component';
import { ToastComponent } from 'src/app/components/toast/toast.component';
import { AdministracionService } from 'src/app/services/administracion.service';
import { FunctionsUtils } from 'src/app/utils/functions.utils';


@Component({
  selector: 'app-crear-empresa',
  templateUrl: './crear-empresa.component.html',
  styleUrls: ['./crear-empresa.component.css']
})
export class CrearEmpresaComponent implements OnInit {
  files: File[] = [];
  dataComprobanteDefectoGuia: any;
  dataComprobanteDefectoVenta: any;
  dataTipoImpresion: any;

  //Campos CreaciÃ³n de Empresa
  ruc: string = '';
  razonSocial: string = '';
  nombreComercial: string = '';
  representanteLegal: string = '';
  direccion: string = '';
  correoElectronico: string = '';
  correoNotificaciones: string = '';
  telefono: string = '';
  usuarioSOL: string = '';
  claveSOL: string = '';
  urlWeb: string = '';
  b64Logo: string = 'data:image/png;base64,';
  comprobanteDefectoGuia: string = '';
  comprobanteDefectoVenta: string = '';
  tipoImpresion: string = '';
  envioAutomaticoSunat: boolean = false;
  precioIncluyeIGV: boolean = false;
  previsualizarPostVenta: boolean = false;
  impresionAutomatica: boolean = false;
  clienteDefectoBoleta: boolean = false;
  mostrarRucNotaVenta: boolean = false;

  toastErrorMessage: string = '';


  constructor(private appComponent: AppComponent, private administracionService: AdministracionService, private functionsUtils: FunctionsUtils, private toastComponent: ToastComponent, private router: Router){
    this.appComponent.login = false;
  }

  ngOnInit(){
    this.fillComprobanteDefectoGuia();
    this.fillComprobanteDefectoVenta();
    this.fillTipoImpresion();
  }

  fillComprobanteDefectoGuia(){
    let data = {
      p_gvo_id: 0,
      p_gvo_active: true
    }

    this.administracionService.postGetComprobanteDefectoGuia(data).subscribe({
      next: (result: any) => {
        this.dataComprobanteDefectoGuia = result;
      },
      error: (error: any) => {
        let toastError = document.querySelector('#toastError') as HTMLDivElement;
        toastError.classList.add('fade', 'show');
        this.toastErrorMessage = error;
        console.error(error);
      }
    });
  }

  fillComprobanteDefectoVenta(){
    let data = {
      p_tsd_id: 0,
      p_tsd_active: true
    }

    this.administracionService.postGetComprobanteDefectoVenta(data).subscribe({
      next: (result: any) => {
        this.dataComprobanteDefectoVenta = result;
      },
      error: (error: any) => {
        let toastError = document.querySelector('#toastError') as HTMLDivElement;
        toastError.classList.add('fade', 'show');
        this.toastErrorMessage = error;
        console.error(error);
      }
    });
  }

  fillTipoImpresion(){
    let data = {
      p_tpr_id: 0,
      p_tpr_active: true
    }

    this.administracionService.postGetTipoImpresion(data).subscribe({
      next: (result: any) => {
        this.dataTipoImpresion = result;
      },
      error: (error: any) => {
        let toastError = document.querySelector('#toastError') as HTMLDivElement;
        toastError.classList.add('fade', 'show');
        this.toastErrorMessage = error;
        console.error(error);
      }
    });
  }

  registerCompany(){
    let data = {
      p_com_id: 0,
      p_com_name: this.razonSocial,
      p_com_rucnum: this.ruc,
      p_com_addres: this.direccion,
      p_com_usrsol: this.usuarioSOL,
      p_com_keysol: this.claveSOL,
      p_com_cornam: this.nombreComercial,
      p_com_legage: this.representanteLegal,
      p_com_notema: this.correoNotificaciones,
      p_com_email: this.correoElectronico,
      p_com_phonum: this.telefono,
      p_com_image: this.b64Logo,
      p_com_flgsun: this.envioAutomaticoSunat,
      p_com_flgigv: this.precioIncluyeIGV,
      p_com_aftsal: this.previsualizarPostVenta,
      p_com_flgbal: this.clienteDefectoBoleta,
      p_com_flgpri: this.impresionAutomatica,
      p_com_flgrba: this.mostrarRucNotaVenta,
      p_com_url: this.urlWeb,
      p_tsd_id: this.comprobanteDefectoVenta,
      p_tpr_id: this.tipoImpresion,
      p_gvo_id: this.comprobanteDefectoGuia,
    }

    this.administracionService.postGetCompanyRegister(data).subscribe({
      next: (data: any) => {
        let result = data[0];
        
        if(result.hasOwnProperty('error')){
          if(result.error != 0){            
            this.toastComponent.showToast('Error al registrar la empresa, intentelo nuevamente.', 'info');
          }else{
            this.wizardChangeTab('finregemp-tab');

            setTimeout(() => {
              this.router.navigate(['/administracion/empresas']);
            }, 3000);
          }
        }else{
          this.toastComponent.showToast('Error al registrar la empresa, intentelo nuevamente.', 'info');
        }
      },
      error: (error: any) => {
        console.log(error);
        this.toastComponent.showToast('Error al registrar la empresa, intentelo nuevamente.', 'info');
      }
    });
  }

  wizardChangeTab(nextTab: string){
    let newTab = document.getElementById(nextTab) as HTMLButtonElement;
    newTab.click();
  }

  onSelect(event: any){
    this.files = [];
    this.files.push(...event.addedFiles);
    if(this.files && this.files[0]){
      for (let i = 0; i < this.files.length; i++){
        this.functionsUtils.fileToBase64(this.files[i]).then((result) => {
          this.b64Logo = result;
        });         
      }
    }
  }

  onRemove(event: any){
    this.b64Logo = 'data:image/png;base64,';
    this.files.splice(this.files.indexOf(event), 1);
  }
}
